import { createRouter, createWebHistory } from 'vue-router'
import Layout from '@/views/layout/Layout.vue'
import { getToken } from '@/utils/auth'
import store from '@/store'

// 白名单路由
const whiteList = ['/login']

// 基础路由
export const constantRoutes = [
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/login/Login.vue'),
    hidden: true
  },
  {
    path: '/404',
    name: '404',
    component: () => import('@/views/error/404.vue'),
    hidden: true
  }
]

// 需要根据权限动态加载的路由
export const asyncRoutes = [
  {
    path: '/system',
    component: Layout,
    redirect: '/system/user',
    name: 'System',
    meta: { 
      title: '系统管理',
      icon: 'Setting',
      roles: ['admin']
    },
    children: [
      {
        path: 'user',
        name: 'User',
        component: () => import('@/views/user/index.vue'),
        meta: { title: '用户管理', icon: 'User' }
      },
      {
        path: 'role',
        name: 'Role',
        component: () => import('@/views/role/index.vue'),
        meta: { title: '角色管理', icon: 'UserFilled' }
      },
      {
        path: 'permission',
        name: 'Permission',
        component: () => import('@/views/permission/index.vue'),
        meta: { title: '权限管理', icon: 'Lock' }
      }
    ]
  },
  {
    path: '/',
    component: Layout,
    redirect: '/dashboard',
    children: [
      {
        path: 'dashboard',
        name: 'Dashboard',
        component: () => import('@/views/dashboard/Dashboard.vue'),
        meta: { title: '仪表盘', icon: 'Menu', roles: ['admin', 'user'] }
      }
    ]
  },
  {
    path: '/assets',
    component: Layout,
    children: [
      {
        path: 'index',
        name: 'Assets',
        component: () => import('@/views/assets/AssetList.vue'),
        meta: { title: '资产管理', icon: 'Document', roles: ['admin', 'user'] }
      }
    ]
  },
  {
    path: '/users',
    component: Layout,
    children: [
      {
        path: 'index',
        name: 'Users',
        component: () => import('@/views/user/index.vue'),
        meta: { title: '人员管理', icon: 'User', roles: ['admin'] }
      }
    ]
  },
  {
    path: '/role',
    component: Layout,
    children: [
      {
        path: 'index',
        name: 'Role',
        component: () => import('@/views/role/index.vue'),
        meta: { title: '角色管理', icon: 'role' }
      },
      {
        path: 'data-scope/:id',
        name: 'DataScope',
        component: () => import('@/views/role/dataScope.vue'),
        meta: { title: '数据权限配置', activeMenu: '/role' },
        hidden: true
      }
    ]
  }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes: constantRoutes
})

export function resetRouter() {
  // 移除所有动态添加的路由
  router.getRoutes().forEach(route => {
    if (route.name && !constantRoutes.find(r => r.name === route.name)) {
      router.removeRoute(route.name)
    }
  })
}

router.beforeEach(async (to, from, next) => {
  const hasToken = getToken()

  if (whiteList.includes(to.path)) {
    if (hasToken && to.path === '/login') {
      next({ path: '/' })
    } else {
      next()
    }
    return
  }

  if (hasToken) {
    try {
      const hasRoles = store.state.user.roles && store.state.user.roles.length > 0
      if (hasRoles) {
        if (to.matched.length === 0) {
          // 重新添加路由
          const accessRoutes = await store.dispatch('permission/generateRoutes', store.state.user.roles)
          accessRoutes.forEach(route => {
            router.addRoute(route)
          })
          // 重新导航到目标路由
          next({ ...to, replace: true })
        } else {
          next()
        }
      } else {
        try {
          // 获取用户信息
          const { roles } = await store.dispatch('user/getInfo')
          // 生成可访问路由
          const accessRoutes = await store.dispatch('permission/generateRoutes', roles)
          // 动态添加路由
          accessRoutes.forEach(route => {
            router.addRoute(route)
          })
          // 重新导航，确保路由已经添加
          next({ ...to, replace: true })
        } catch (error) {
          // 清除所有状态并跳转到登录页
          await store.dispatch('user/logout')
          next(`/login?redirect=${to.path}`)
        }
      }
    } catch (error) {
      console.error('Route Error:', error)
      await store.dispatch('user/logout')
      next(`/login?redirect=${to.path}`)
    }
  } else {
    next(`/login?redirect=${to.path}`)
  }
})

export default router
