import request from '@/utils/request'

export function login(data) {
  return request({
    url: 'auth-token/',
    method: 'post',
    data: {  // 确保数据格式正确
      username: data.username,
      password: data.password
    }
  })
}

export function getInfo() {
  return request({
    url: 'users/me/',
    method: 'get'
  })
}

export function logout() {
  return request({
    url: 'auth-token/logout/',
    method: 'post'
  })
}
