import { login, getInfo } from '@/api/user'
import { getToken, setToken, removeToken } from '@/utils/auth'
import { resetRouter } from '@/router'

const getDefaultState = () => {
  return {
    token: getToken(),
    name: '',
    avatar: '',
    roles: ['user']  // 设置默认角色
  }
}

const state = getDefaultState()

const mutations = {
  RESET_STATE: (state) => {
    Object.assign(state, getDefaultState())
  },
  SET_TOKEN: (state, token) => {
    state.token = token
  },
  SET_NAME: (state, name) => {
    state.name = name
  },
  SET_AVATAR: (state, avatar) => {
    state.avatar = avatar
  },
  SET_ROLES: (state, roles) => {
    state.roles = Array.isArray(roles) ? roles : [roles]  // 确保是数组
  }
}

const actions = {
  // 登录
  login({ commit }, userInfo) {
    const { username, password } = userInfo
    return new Promise((resolve, reject) => {
      login({ username: username.trim(), password: password })
        .then(response => {
          const { token } = response
          commit('SET_TOKEN', token)
          setToken(token)
          resolve()
        })
        .catch(error => {
          reject(error)
        })
    })
  },

  // 获取用户信息
  getInfo({ commit }) {
    return new Promise((resolve, reject) => {
      getInfo()
        .then(response => {
          if (!response) {
            reject('验证失败，请重新登录')
          }

          const { roles = ['user'], name, avatar } = response

          // 确保 roles 是数组
          if (!Array.isArray(roles)) {
            reject('角色必须是数组!')
          }

          commit('SET_ROLES', roles)
          commit('SET_NAME', name)
          commit('SET_AVATAR', avatar)
          resolve(response)
        })
        .catch(error => {
          reject(error)
        })
    })
  },

  // 登出
  logout({ commit, dispatch }) {
    return new Promise(async (resolve) => {
      try {
        // 先清除所有状态
        await dispatch('resetState', null, { root: true })  // 清除根状态
        await dispatch('permission/resetRoutes', null, { root: true })  // 清除权限路由
        await dispatch('app/resetState', null, { root: true })  // 清除应用状态
        
        // 移除 token
        removeToken()
        // 重置路由
        resetRouter()
        // 重置用户状态
        commit('RESET_STATE')
        
        resolve()
      } catch (error) {
        console.error('Logout failed:', error)
        resolve()
      }
    })
  },

  // 重置状态
  resetState({ commit }) {
    return new Promise(resolve => {
      commit('RESET_STATE')
      resolve()
    })
  },

  // 重置 Token
  resetToken({ commit }) {
    return new Promise(resolve => {
      removeToken() // 必须先移除 token
      commit('RESET_STATE')
      resolve()
    })
  }
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}
