import request from '@/utils/request'

export function login(data) {
  return request({
    url: '/api/token-auth/',
    method: 'post',
    data
  })
}

export function getInfo() {
  return request({
    url: '/api/users/info/',
    method: 'get'
  })
}

export function getUserList(params) {
  return request({
    url: '/api/users/',
    method: 'get',
    params
  })
}

export function getDevelopers() {
  return request({
    url: '/api/users/developers/',
    method: 'get'
  })
}

export function getManagers() {
  return request({
    url: '/api/users/managers/',
    method: 'get'
  })
}

export function createUser(data) {
  return request({
    url: '/api/users/',
    method: 'post',
    data
  })
}

export function updateUser(id, data) {
  return request({
    url: `/api/users/${id}/`,
    method: 'put',
    data
  })
}

export function deleteUser(id) {
  return request({
    url: `/api/users/${id}/`,
    method: 'delete'
  })
}

export function lockUser(id, reason) {
  return request({
    url: `/api/users/${id}/lock/`,
    method: 'post',
    data: { reason }
  })
}

export function unlockUser(id, reason) {
  return request({
    url: `/api/users/${id}/unlock/`,
    method: 'post',
    data: { reason }
  })
}

export function changePassword(id, data) {
  return request({
    url: `/api/users/${id}/change_password/`,
    method: 'post',
    data
  })
}
