import { createRouter, createWebHistory } from 'vue-router'
import Layout from '@/views/layout/Layout.vue'
import { getToken } from '@/utils/auth'
import store from '@/store'

// 基础路由
export const constantRoutes = [
  {
    path: '/login',
    name: 'Login',
    component: () => import('@/views/login/Login.vue'),
    meta: { title: '登录' }
  },
  {
    path: '/404',
    component: () => import('@/views/error/404.vue'),
    meta: { title: '404' }
  },
  {
    path: '/',
    component: Layout,
    redirect: '/dashboard',
    meta: { title: '主页' },
    children: [
      {
        path: 'dashboard',
        name: 'Dashboard',
        component: () => import('@/views/dashboard/Dashboard.vue'),
        meta: { title: '仪表盘', icon: 'Menu', cache: true, affix: true }
      }
    ]
  }
]

// 需要根据权限动态加载的路由
export const asyncRoutes = [
  {
    path: '/assets',
    component: Layout,
    meta: { title: '资产管理' },
    children: [
      {
        path: 'index',
        name: 'Assets',
        component: () => import('@/views/assets/AssetList.vue'),
        meta: { title: '资产管理', icon: 'Document', roles: ['admin', 'user'] }
      }
    ]
  },
  {
    path: '/users',
    component: Layout,
    meta: { title: '用户管理' },
    children: [
      {
        path: 'index',
        name: 'Users',
        component: () => import('@/views/user/index.vue'),
        meta: { title: '用户管理', icon: 'User', roles: ['admin'] }
      }
    ]
  },
  // 404 页面必须放在最后
  { path: '/:pathMatch(.*)*', redirect: '/404', hidden: true }
]

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes: constantRoutes
})

export function resetRouter() {
  const newRouter = createRouter({
    history: createWebHistory(process.env.BASE_URL),
    routes: constantRoutes
  })
  router.matcher = newRouter.matcher
}

router.beforeEach(async (to, from, next) => {
  const hasToken = getToken()

  if (hasToken) {
    if (to.path === '/login') {
      next({ path: '/' })
    } else {
      try {
        // 确保 roles 存在且是数组
        const roles = store.state.user.roles || []
        const hasRoles = Array.isArray(roles) && roles.length > 0

        if (hasRoles) {
          next()
        } else {
          // 获取用户信息
          const { roles } = await store.dispatch('user/getInfo')
          
          // 确保 roles 是数组
          const userRoles = Array.isArray(roles) ? roles : [roles]
          
          // 生成可访问路由
          const accessRoutes = await store.dispatch('permission/generateRoutes', userRoles)
          
          // 动态添加路由
          accessRoutes.forEach(route => {
            router.addRoute(route)
          })

          next({ ...to, replace: true })
        }
      } catch (error) {
        console.error('Route Error:', error)
        await store.dispatch('user/resetToken')
        next(`/login?redirect=${to.path}`)
      }
    }
  } else {
    if (to.path === '/login') {
      next()
    } else {
      next(`/login?redirect=${to.path}`)
    }
  }
})

export default router
