import { login, logout, getInfo } from '@/api/user'
import { getToken, setToken, removeToken } from '@/utils/auth'
import { resetRouter } from '@/router'
import { clearPermissionCache } from '@/utils/permission-cache'

const state = {
  token: getToken(),
  name: '',
  avatar: '',
  roles: [],
  permissions: []
}

const mutations = {
  SET_TOKEN: (state, token) => {
    state.token = token
  },
  SET_NAME: (state, name) => {
    state.name = name
  },
  SET_AVATAR: (state, avatar) => {
    state.avatar = avatar
  },
  SET_ROLES: (state, roles) => {
    state.roles = roles
  },
  SET_PERMISSIONS: (state, permissions) => {
    state.permissions = permissions
  },
  RESET_STATE: (state) => {
    state.token = ''
    state.name = ''
    state.avatar = ''
    state.roles = []
    state.permissions = []
  }
}

const actions = {
  // 用户登录
  login({ commit }, userInfo) {
    const { username, password } = userInfo
    return new Promise((resolve, reject) => {
      login({ username: username.trim(), password: password })
        .then(response => {
          const { token, user } = response
          commit('SET_TOKEN', token)
          commit('SET_NAME', user.name)
          commit('SET_AVATAR', user.avatar)
          commit('SET_ROLES', [user.role]) // 使用用户角色
          commit('SET_PERMISSIONS', user.permissions)
          setToken(token)
          resolve(user)
        })
        .catch(error => {
          reject(error)
        })
    })
  },

  // 获取用户信息
  getInfo({ commit }) {
    return new Promise((resolve, reject) => {
      getInfo()
        .then(response => {
          if (!response) {
            reject('验证失败，请重新登录')
            return
          }

          const { roles, name, avatar, permissions } = response

          if (!roles || roles.length <= 0) {
            reject('用户角色不能为空')
            return
          }

          commit('SET_ROLES', roles)
          commit('SET_NAME', name)
          commit('SET_AVATAR', avatar)
          commit('SET_PERMISSIONS', permissions)
          resolve(response)
        })
        .catch(error => {
          reject(error)
        })
    })
  },

  // 用户登出
  logout({ commit }) {
    return new Promise((resolve, reject) => {
      logout()
        .then(() => {
          commit('RESET_STATE')
          removeToken()
          resetRouter()
          clearPermissionCache()
          resolve()
        })
        .catch(error => {
          reject(error)
        })
    })
  },

  // 重置 token
  resetToken({ commit }) {
    return new Promise(resolve => {
      commit('RESET_STATE')
      removeToken()
      resolve()
    })
  }
}

export default {
  namespaced: true,
  state,
  mutations,
  actions
}
